import React, { useState } from "react";
import { Link } from "react-router-dom";
/* style media and icons */
import styles from "../views/Menu.module.scss";
import { BiMenu, BiX } from "react-icons/bi";
import { BsFillPersonFill } from "react-icons/bs";
import logoCompany from "../media/picture/logo.jpg"
/* components */
import Container from "../components/Container";

export default function Menu() {
    const [isActive, setActive] = useState("close");
    // const [isClose, setClose] = useState('open');
    const [stanUser, setStanUser] = useState('close')

    const logOut = () => {
        localStorage.clear();
        setInterval();
    };

    const menuHamburgerStan=()=>{
        if(isActive === "open"){
            setActive("close")
        }else if(isActive === "close"){
            setActive("open")
        }
    }

    const userOption = () => {
        if (stanUser === 'close') {
            setStanUser('open')
        } else if (stanUser === 'open') {
            setStanUser('close')
        }
    };
    return (
        <Container thirdContainer={true}>
            <nav className={styles.mainNav}>
                <div className={styles[isClose]} onClick={() => menuHamburgerStan()}>
                    <BiMenu className={styles.iconOpen} />
                </div>
                <div className={styles[isActive]}>
                    <div className={styles.contentOpenMenu}>
                        <div className={styles.closeMobileHamburger} onClick={() => menuHamburgerStan()}>
                            <BiX className={styles.iconClose} />
                        </div>
                        <ul className={styles.menu}>
                            <li>
                                <Link className={styles.mainItem} to="/signup">Rejestracja</Link>
                            </li>

                            <li>
                                <button className={styles.mainItem} onClick={logOut}>Wyloguj</button>
                            </li>
                            <li>
                                <Link className={styles.mainItem} to="/signup">Rejestracja</Link>
                            </li>

                            <li>
                                <button className={styles.mainItem} onClick={logOut}>Wyloguj</button>
                            </li>
                        </ul>
                    </div>

                </div>
            </nav>
            <img src={logoCompany} alt="pictureLogo" />

            <div className={styles.contentLinkMenu}>
                <BsFillPersonFill onClick={() => userOption()} />
                <div className={styles[stanUser]}>
                    <Link className={styles.mainItem} to="/signup">Logowanie</Link>
                    <Link className={styles.mainItem} to="/signup">Rejestracja</Link>
                </div>
            </div>
            {/* <button className={styles.mainItem} onClick={logOut}>Wyloguj</button> */}



        </Container>

    )
}
