import React, { useState } from "react";
import axios from "axios";
/* components */
import Container from "../components/Container";
import MainHeder from "../components/MainHeder";
import Button from "../components/Button";
/* style */
import styles from "../views/LoginAndSignUp.module.scss";

export default function LoginAndSignUp(props) {
    const [registration, setRegistration] = useState("");
    const [classFirstName, setClassFirstName] = useState("");
    const [classLastName, setClassLastName] = useState("");
    const [classNumberId, setClassNumberId] = useState("");
    const [classPhoneNumber, setCallsPhoneNumber] = useState("");
    const [classCity, setClassCity] = useState("");
    const [classStreet, setClassStreet] = useState("");
    const [classNumber, setClassNumber] = useState("");
    const [classZipCode, setClassZipCode] = useState("");
    const [classEmail, setClassEmail] = useState("");
    const [classPassword, setClassPassword] = useState("");
    const [classRepPassword, setClassRepPassword] = useState("");
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [numberId, setNumberId] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [city, setCity] = useState("");
    const [street, setStreet] = useState("");
    const [number, setNumber] = useState("");
    const [zipCode, setZipCode] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [repPassword, setRepPassword] = useState("");
    const [error, setError] = useState("");

    const registrationUser = () => {
        if (!firstName) {
            setError("Wpisz swoje imię");
            setClassFirstName("errorClass");
        } else if (!lastName) {
            setError("Wpisz swoje nazwisko");
            setClassFirstName("correctClass");
            setClassLastName("errorClass");
        } else if (!numberId) {
            setError("Wpisz numer PESEL");
            setClassLastName("correctClass");
            setClassNumberId("errorClass");
            setClassFirstName("");
        } else if (numberId.length <= 10) {
            setError("Podany numer jest zbyt krótki");
            setClassNumberId("errorClass");
        } else if (numberId.length >= 12) {
            setError("Podany numer jest za długi");
            setClassNumberId("errorClass");
        } else if (/\D/.test(numberId)) {
            setError("Podałeś błedny znak numer PESEL skłąda się z samych cyfr");
            setClassNumberId("errorClass");
        } else if (!phoneNumber) {
            setError("Wpisz numer telefonu");
            setClassNumberId("correctClass");
            setClassLastName("");
            setCallsPhoneNumber("errorClass");
        } else if (/\D/.test(phoneNumber)) {
            setError("Podałeś błedny znak, wprowadź same cyfry")
            setClassNumberId("correctClass");
            setCallsPhoneNumber("errorClass");
        } else {
            const formData = {
                personalData: {
                    firstName: firstName,
                    lastName: lastName,
                    numberId: numberId
                },
                address: {
                    city: city,
                    street: street,
                    number: number,
                    zipCode: zipCode
                },
                phoneNumber: phoneNumber,
                email: email,
                password: password

            }
            axios.post('http://127.0.0.1:8080/user/signup', formData)
                .then(() => {
                    setError("Zostałeś zarejestrowany możesz sie teraz zalogować")
                })
            setFirstName("");
            setLastName("")
            setNumberId("");
            setCity("");
            setStreet("");
            setNumber("");
            setZipCode("");
            setPhoneNumber("");
            setEmail("");
            setPassword("");
            setRepPassword("");
            setRegistration("")

        }

    }
    const loginUser = () => {
        axios.post("http://127.0.0.1:8080/user/login", {
            email: email,
            password: password
        })
            .then((req) => {
                if (!req.data.success) {
                    setError('Podane dane logowania są nieprawidłowe');
                } else {
                    props.setUser(req.data);
                    localStorage.setItem("user", JSON.stringify(req.data));
                }
            })
    };

    if (registration === "openRegistration") {
        return (
            <Container secundContainer={true}>
                <MainHeder>Rejestracja</MainHeder>
                <p>{error}</p>
                <form>
                    <label>
                        <span>Dane personalne</span>
                        <div>
                            <input
                                className={styles[classFirstName]}
                                value={firstName}
                                onChange={(e) => setFirstName(e.target.value)}
                                placeholder="Wpisz swoje imię" />
                            <input
                                className={styles[classLastName]}
                                value={lastName}
                                onChange={(e) => setLastName(e.target.value)}
                                placeholder="Wpisz swoje nazwisko" />
                            <input
                                className={styles[classNumberId]}
                                value={numberId}
                                onChange={(e) => setNumberId(e.target.value)}
                                placeholder="Pesel" />
                            <input
                                className={styles[classPhoneNumber]}
                                value={phoneNumber}
                                onChange={(e) => setPhoneNumber(e.target.value)}
                                placeholder="Numer kontaktowy" />
                        </div>
                    </label>
                    <label>
                        <span>Adres</span>
                        <div>
                            <input
                                className={styles[classCity]}
                                value={city}
                                onChange={(e) => setCity(e.target.value)}
                                placeholder="miasto" />
                            <input
                                className={styles[classStreet]}
                                value={street}
                                onChange={(e) => setStreet(e.target.value)}
                                placeholder="ulica" />
                            <input
                                className={styles[classNumber]}
                                value={number}
                                onChange={(e) => setNumber(e.target.value)}
                                placeholder="numer domu" />
                            <input
                                className={styles[classZipCode]}
                                value={zipCode}
                                onChange={(e) => setZipCode(e.target.value)}
                                placeholder="kod pocztowy" />
                        </div>

                    </label>

                    <label>
                        <span>Dane konta</span>
                        <div>
                            <input
                                className={styles[classEmail]}
                                onChange={(e) => setEmail(e.target.value)}
                                value={email}
                                placeholder="emial" />
                            <input
                                className={styles[classPassword]}
                                onChange={(e) => setPassword(e.target.value)}
                                value={password}
                                placeholder="hasło" />
                            <input
                                className={styles[classRepPassword]}
                                onChange={(e) => setRepPassword(e.target.value)}
                                value={repPassword}
                                placeholder="powtórz hasło" />
                        </div>

                    </label>
                    <div>
                        <Button type="submit" onClick={(e) => {
                            e.preventDefault();
                            registrationUser()
                        }}>Zarejestruj</Button>
                        <Button onClick={() => {
                            setRegistration("");
                            setError("")
                        }}>Powrót do logowania</Button>
                    </div>

                </form>
            </Container>
        )
    }
    return (
        <Container secundContainer={true}>
            <MainHeder>Logowanie</MainHeder>
            <p>{error}</p>
            <form>
                <input
                    className={styles[classEmail]}
                    onChange={(e) => setEmail(e.target.value)}
                    value={email}
                    placeholder="Login" />
                <input
                    className={styles[classPassword]}
                    onChange={(e) => setPassword(e.target.value)}
                    value={password}
                    placeholder="Hasło" />
                <Button type="submit" onClick={(e) => {
                    e.preventDefault();
                    loginUser()
                }}>Zaloguj</Button>
            </form>
            <p>Aby stworzyć konto kliknij <Button secundBtn={true} onClick={() => {
                setRegistration("openRegistration");
                setError("")
            }}>tutaj</Button></p>
        </Container>
    )
}
