import React, { useState } from "react";
/* components */
import Container from "../components/Container";
import MainHeder from "../components/MainHeder";
import Button from "../components/Button";
/* style */
import "../views/LoginAndSignUp.module.scss";

export default function LoginAndSignUp() {
    const [registration, setRegistration] = useState("");


    const openRegistration = () => {
        setRegistration("openRegistration")
        console.log("działa");
    }

    if (registration === "openRegistration") {
        return (
            <Container secundContainer={true}>
                <MainHeder>Rejestracja</MainHeder>
                <form>
                    <label>
                        Dane personalne
                        <input placeholder="Wpisz swoje imię" />
                        <input placeholder="Wpisz swoje nazwisko" />
                        <input placeholder="Pesel" />
                        <input placeholder="Numer kontaktowy" />
                    </label>
                    <label>
                        Adres
                        <input placeholder="miasto" />
                        <input placeholder="ulica" />
                        <input placeholder="numer domu" />
                        <input placeholder="kod pocztowy" />
                    </label>

                    <label>
                        Dane konta
                        <input placeholder="emial" />
                        <input placeholder="hasło" />
                        <input placeholder="powtórz hasło" />
                    </label>
                    <div>
                        <Button>Zarejestruj</Button>
                        <Button>Powrót do logowania</Button>
                    </div>
                    
                </form>
            </Container>
        )
    }
    return (
        <Container secundContainer={true}>
            <MainHeder>Logowanie</MainHeder>
            <form>
                <input placeholder="Login" />
                <input placeholder="Podaj hasło" />
                <Button>Zaloguj</Button>
            </form>
            <p>Aby stworzyć konto kliknij <Button secundBtn={true} onClick={() => openRegistration()}>tutaj</Button></p>
        </Container>
    )
}
