import React, { useState, useEffect } from "react";
import axios from "axios";
/* components and style */
import styles from "../views/ToolList.module.scss";
import Container from "../components/Container";
import MainHeder from "../components/MainHeder";
import Button from "../components/Button";

export default function ToolList() {
    const [listEquipment, setListEquipment] = useState([]);
    const [views, setViews] = useState("all")
    const equipmentList = () => {
        axios.post("http://127.0.0.1:8080/equipment/all")
            .then((res) => {
                setListEquipment(res.data)
            })
    };
    useEffect(() => {
        equipmentList()
    }, [])
    return (
        <Container sevenContainer={true}>
            <MainHeder secundHeder={true}>Nasza Oferta</MainHeder>
            <Container sixContainer={true}>
                <div className={styles.listMachine}>
                    <div>
                        <select className={styles.select} onChange={(e) => setViews(e.target.value)}>
                            <option value="all">Wszystkie</option>
                            <option value="Elektryczne">Elektryczne</option>
                            <option value="Hydrauliczne">Hydrauliczne</option>
                            <option value="Cięzki Sprzęt">Cięzki Sprzęt</option>
                            <option value="Inne">Inne</option>
                        </select>
                    </div>
                    <p>Kategoria</p>

                    {listEquipment.map((nameMachine) => {
                        return (
                            <Button key={nameMachine._id} thirdBtn={true}>{nameMachine.machineName}</Button>
                        )
                    })}
                </div>
                <div className={styles.clickMachine}>
                    <p>wybrana maszyna plus zamów</p>
                </div>
            </Container>

        </Container>
    )
}
