import React, { useState } from "react";
import axios from "axios";
/* components */
import Container from "../components/Container";
import MainHeder from "../components/MainHeder";
import Button from "../components/Button";
import Error from "../components/Error";
/* style */
import "../views/LoginAndSignUp.module.scss";

export default function LoginAndSignUp(props) {
    const [registration, setRegistration] = useState("");
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [numberId, setNumberId] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [city, setCity] = useState("");
    const [street, setStreet] = useState("");
    const [number, setNumber] = useState("");
    const [zipCode, setZipCode] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [repPassword, setRepPassword] = useState("");
    const [error, setError] = useState("");

    const registrationUser = (e) => {
   /*      if (!firstName) {
            setError("Wpisz swoje imię");
        } else if (!lastName) {
            setError("Wpisz swoje nazwisko");
        } else if (!numberId) {
            setError("Wpisz numer PESEL");
        } else if (numberId.length <= 10) {
            setError("Podany numer jest zbyt krótki");
        } else if (numberId.length >= 12) {
            setError("Podany numer jest za długi");
        } else if (/\D/.test(numberId)) {
            setError("Podałeś błedny znak numer PESEL skłąda się z samych cyfr");
        } else if (!phoneNumber) {
            setError("Wpisz numer telefonu");
        } else if (/\D/.test(phoneNumber)) {
            setError("Podałeś błedny znak, wprowadź same cyfry")
        } else if (!city) {
            setError("Wprowadź miasto");
        } else if (!street) {
            setError("Wprowadź ulice");
        } else if (!zipCode) {
            setError("Wprowadź kod poczowy")
        } else */ if (!email) {
            setError("Wprowadaź adres email");
        } else if ((!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(email))) {
            setError("Wprowadaź porpawny email");
        } else if (!password) {
            setError("Wprowadź hasło");
        } else if (password !== repPassword) {
            setError("Podane hasła nie sa identyczne");
        } else {

            const formData = {
                personalData: {
                    firstName: firstName,
                    lastName: lastName,
                    numberId: numberId
                },
                address: {
                    city: city,
                    street: street,
                    number: number,
                    zipCode: zipCode
                },
                phoneNumber: phoneNumber,
                email: email,
                password: password

            }

            axios.post('http://127.0.0.1:8080/user/signup', formData, {
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify({ email: email })
            })


                .then(res => res.json())
                .then((data) => {
                    if (data.exists) {
                        setError(<Error>Podany adres email istnieje w bazie</Error>)
                    } else {
                        setError(<Error isAlternative={true}>Zostałeś zarejestrowany możesz sie teraz zalogować</Error>)

                    }


                })
            setFirstName("");
            setLastName("")
            setNumberId("");
            setCity("");
            setStreet("");
            setNumber("");
            setZipCode("");
            setPhoneNumber("");
            setEmail("");
            setPassword("");
            setRepPassword("");
            setRegistration("")



        }

    }
    const loginUser = () => {
        if (!email) {
            setError("Wprowadaź adres email");
        } else if ((!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(email))) {
            setError("Wprowadaź porpawny email");
        } else if (!password) {
            setError("Wprowadź hasło");
        } else {
            axios.post("http://127.0.0.1:8080/user/login", {
                email: email,
                password: password
            })
                .then((req) => {
                    if (!req.data.success) {
                        setError('Podane dane logowania są nieprawidłowe');
                    } else {
                        props.setUser(req.data);
                        localStorage.setItem("user", JSON.stringify(req.data));
                    }
                })
        }
    };

    if (registration === "openRegistration") {
        return (
            <Container secundContainer={true}>
                <MainHeder>Rejestracja</MainHeder>
                <Error>{error}</Error>
                <form>
                    <label>
                        <span>Dane personalne</span>
                        <div>
                            <input
                                type="text"
                                value={firstName}
                                onChange={(e) => setFirstName(e.target.value)}
                                placeholder="Wpisz swoje imię" />
                            <input
                                type="text"
                                value={lastName}
                                onChange={(e) => setLastName(e.target.value)}
                                placeholder="Wpisz swoje nazwisko" />
                            <input
                                type="text"
                                value={numberId}
                                onChange={(e) => setNumberId(e.target.value)}
                                placeholder="Pesel" />
                            <input
                                type="text"
                                value={phoneNumber}
                                onChange={(e) => setPhoneNumber(e.target.value)}
                                placeholder="Numer kontaktowy" />
                        </div>
                    </label>
                    <label>
                        <span>Adres</span>
                        <div>
                            <input
                                type="text"
                                value={city}
                                onChange={(e) => setCity(e.target.value)}
                                placeholder="miasto" />
                            <input
                                type="text"
                                value={street}
                                onChange={(e) => setStreet(e.target.value)}
                                placeholder="ulica" />
                            <input
                                type="text"
                                value={number}
                                onChange={(e) => setNumber(e.target.value)}
                                placeholder="numer domu" />
                            <input
                                type="text"
                                value={zipCode}
                                onChange={(e) => setZipCode(e.target.value)}
                                placeholder="kod pocztowy" />
                        </div>

                    </label>

                    <label>
                        <span>Dane konta</span>
                        <div>
                            <input
                                type="text"
                                onChange={(e) => setEmail(e.target.value)}
                                value={email}
                                placeholder="emial" />
                            <input
                                type="password"
                                onChange={(e) => setPassword(e.target.value)}
                                value={password}
                                placeholder="hasło" />
                            <input
                                type="password"
                                onChange={(e) => setRepPassword(e.target.value)}
                                value={repPassword}
                                placeholder="powtórz hasło" />
                        </div>

                    </label>
                    <div>
                        <Button type="submit" onClick={(e) => {
                            e.preventDefault();
                            registrationUser()
                        }}>Zarejestruj</Button>
                        <Button onClick={() => {
                            setRegistration("");
                            setError("")
                        }}>Powrót do logowania</Button>
                    </div>

                </form>
            </Container>
        )
    }
    return (
        <Container secundContainer={true}>
            <MainHeder>Logowanie</MainHeder>
            <Error>{error}</Error>
            <form>
                <input
                    type="text"
                    onChange={(e) => setEmail(e.target.value)}
                    value={email}
                    placeholder="Login" />
                <input
                    type="password"
                    onChange={(e) => setPassword(e.target.value)}
                    value={password}
                    placeholder="Hasło" />
                <Button type="submit" onClick={(e) => {
                    e.preventDefault();
                    loginUser()
                }}>Zaloguj</Button>
            </form>
            <p>Aby stworzyć konto kliknij <Button secundBtn={true} onClick={() => {
                setRegistration("openRegistration");
                setError("")
            }}>tutaj</Button></p>
        </Container>
    )
}
