import React, { useState, useEffect } from "react"
import axios from "axios";
/* components and style */
import MainSecondHeder from "../components/MainSecoundHeder";
export default function ListUser() {
    const [listUser, setUser] = useState([])
    const [role, setRole] = useState("admin")
    const userList = () => {
        axios.post("http://127.0.0.1:8080/user/all")
            .then((res) => {
                setUser(res.data)
            })
    }
    useEffect(() => {
        userList()
    }, [])
    return (
        <div>
            <MainSecondHeder>Lista Użytkowników</MainSecondHeder>
            <table>
                {/* <select>
                    <option >wybierz</option>
                    <option onChange={setRole("employee")}>Pracownivy</option>
                </select> */}
                <thead>
                    <tr>
                        <td>Imię</td>
                        <td>Numer Kontaktowy</td>
                        <td>Emial</td>
                        <td>Akcje</td>
                    </tr>
                </thead>
                <tbody>
                    {listUser.map((user) => {
                        if (user.role === role) {
                            return (
                                <tr key={user._id}>
                                    <td>
                                        {user.personalData.firstName}
                                    </td>
                                    <td>{user.phoneNumber}</td>
                                    <td>{user.email}</td>
                                </tr>
                            );
                        }
                        return null;
                    })}
                </tbody>
            </table>
        </div>


    )
}
