import React, { useState } from "react";
import { Link } from "react-router-dom";
/* style and media*/
import styles from "../views/Menu.module.scss";
import { BiMenu, BiX } from "react-icons/bi";

export default function Menu() {
    const [isActive, setActive] = useState("open")
    const [isClose, setClose] = useState('close');

    const logOut = () => {
        localStorage.clear();
        setInterval();
    };

    const onOpen = () => {
        setActive('open')
        setClose('close')
    }
    const onClose = () => {
        setClose('mobileHamburger')
        setActive('close')
    }
    return (
        <nav className={styles.mainNav}>
            <div className={styles[isClose]} onClick={() => onOpen()}>
                <BiMenu className={styles.iconOpen} />
            </div>
            <div className={styles[isActive]}>
                <div className={styles.closeMobileHamburger} onClick={() => onClose()}>
                    <BiX className={styles.iconClose} />
                </div>
                <ul className={styles.menu}>
                    <li>
                        <Link className={styles.mainItem} to="/signup">Rejestracja</Link>
                    </li>
                </ul>
                <ul className={styles.menu}>
                    <li>
                        <button className={styles.mainItem} onClick={logOut}>Wyloguj</button>
                    </li>
                </ul>
            </div>
        </nav>
    )
}
