import React, { useState, useEffect } from "react"
import axios from "axios";
/* components and style */
import MainSecondHeder from "../components/MainSecoundHeder";
import Button from "../components/Button";
export default function ListUser() {
    const [listUser, setUser] = useState([]);
    const [role, setRole] = useState("admin");
    const [views, setViews] = useState("");
    const [oneUser, setOneUser] = useState({
        personalData: [],
        address: []
    });

    const userList = () => {
        axios.post("http://127.0.0.1:8080/user/all")
            .then((res) => {
                setUser(res.data)
            })
    };

    const userOne = (_id) => {
        axios.get("http://127.0.0.1:8080/user/" + _id)
            .then((res) => {
                setOneUser(res.data)
            })
    }

    const tableName = () => {
        if (role === "employee") {
            return "Lista Pracowników"
        } else if (role === "client") {
            return "Lista Klijetów"
        } else {
            return "Administratorzy strony"
        }
    }
    useEffect(() => {
        userList()
    }, [])

    if (views === "oneUser") {
        if (oneUser.role === "client") {
            return (
                <p>klijet </p>
            )
        } else if(oneUser.role === "admin" || oneUser.role === "employee") {
            <p>pracownik</p>
        }
        return null

    }
    return (
        <div>
            <MainSecondHeder>Lista Użytkowników</MainSecondHeder>
            <div>
                <Button onClick={() => setRole('admin')}>Administratorzy</Button>
                <Button onClick={() => setRole('employee')}>Pracownicy</Button>
                <Button onClick={() => setRole('client')}>Klięci</Button>
            </div>
            <table>
                <thead>
                    <tr>
                        <td><p>{tableName()}</p> </td>
                    </tr>
                    <tr>
                        <td>Imię</td>
                        <td>Numer Kontaktowy</td>
                        <td>Emial</td>
                        <td>Akcje</td>
                    </tr>
                </thead>
                <tbody>
                    {listUser.map((user) => {
                        if (user.role === role) {
                            return (
                                <tr key={user._id}>
                                    <td>
                                        {user.personalData.firstName}
                                    </td>
                                    <td>{user.phoneNumber}</td>
                                    <td>{user.email}</td>
                                    <td><Button onClick={() => {
                                        userOne(user._id)
                                        setViews("oneUser")
                                    }}>Wiecej informacji</Button></td>
                                </tr>
                            );
                        }
                        return null;
                    })}
                </tbody>
            </table>

        </div>


    )
}
